INSERT INTO "cloud_customer_uat"."config_value" VALUES (1511146700366525, 1472543466435, 'SELECT 
?1 AS LOCK_ID, 
CASE WHEN T1.ASSET_DEPR_METHOD_ID = 10005 THEN T1.UNIT_PROD_DEPR 
WHEN T1.CALCULATE_COST - T1.UNIT_DEPR < CALC_IN_DEPR_AMT THEN ( CASE WHEN T1.CALCULATE_COST - T1.UNIT_DEPR > 0 THEN T1.CALCULATE_COST - T1.UNIT_DEPR ELSE 0 END) ELSE T1.CALC_IN_DEPR_AMT END AS IN_DEPR_AMT, 
CASE WHEN T1.ASSET_DEPR_METHOD_ID = 10005 THEN T1.UNIT_PROD_DEPR 
WHEN T1.CALCULATE_COST - T1.TAX_DEPR_AMT < CALC_ST_IN_DEPR_AMT THEN ( CASE WHEN T1.CALCULATE_COST - T1.TAX_DEPR_AMT > 0 THEN T1.CALCULATE_COST - T1.TAX_DEPR_AMT ELSE 0 END) ELSE T1.CALC_ST_IN_DEPR_AMT END AS STANDARD_IN_DEPR_AMT, 
T1.* 
FROM 
( SELECT 
NVL(CASE WHEN T0.CALCULATE_COST = T0.UNIT_DEPR OR T0.USAGE_YEAR = 0 THEN 0 ELSE (T0.CALCULATE_COST / T0.USAGE_YEAR * T0.DAY_OF_DEPR) END,0) AS CALC_IN_DEPR_AMT, 
NVL(CASE WHEN CALC_DEPR_COST = T0.TAX_DEPR_AMT OR T0.ST_USAGE_YEAR = 0 THEN 0 ELSE (T0.CALCULATE_COST / T0.ST_USAGE_YEAR * T0.DAY_OF_DEPR) END,0) AS CALC_ST_IN_DEPR_AMT, 
T0.* FROM ( SELECT (LINE.IN_COST * LINE.BAL_QTY) - (LINE.SALVAGE_AMT * LINE.BAL_QTY) AS CALC_DEPR_COST,', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
