INSERT INTO "cloud_customer_uat"."tmp_export_table_ddl" VALUES ('ERROR_CUSTOMER', 'TRIGGER', '
  CREATE OR REPLACE EDITIONABLE TRIGGER "DEVPROD"."ERROR_CUSTOMER" 
BEFORE
  INSERT OR
  UPDATE
  OF CUSTOMER_ID,PERSON_ID
  ON CRM_CUSTOMER
  FOR EACH ROW
    WHEN (NEW.PERSON_ID IS NOT NULL) DECLARE
    PRAGMA AUTONOMOUS_TRANSACTION;
    V_COUNT NUMBER(11);
    V_CUSTOMER_ID CRM_CUSTOMER.CUSTOMER_ID%TYPE;
    V_PERSON_ID NUMBER(18);
BEGIN

  V_CUSTOMER_ID := :NEW.CUSTOMER_ID;
  V_PERSON_ID := :NEW.PERSON_ID;
  
  SELECT
    COUNT(1) INTO V_COUNT
  FROM CRM_CUSTOMER T1
  WHERE T1.PERSON_ID = V_PERSON_ID AND
    T1.CUSTOMER_ID != V_CUSTOMER_ID;
 
  IF V_COUNT > 0 THEN
    RAISE_APPLICATION_ERROR(-20000, ''ALREADY EXISTS THIS CUSTOMER'');
  END IF;

END;

/
ALTER TRIGGER "DEVPROD"."ERROR_CUSTOMER" DISABLE;');
