INSERT INTO "cloud_customer_uat"."tmp_export_table_ddl" VALUES ('TRIG_ORG_DEPARTMENT_HIER_ORDER', 'TRIGGER', '
  CREATE OR REPLACE EDITIONABLE TRIGGER "DEVPROD"."TRIG_ORG_DEPARTMENT_HIER_ORDER" 
before 
  INSERT OR
  UPDATE
  ON ORG_DEPARTMENT
  FOR EACH ROW
  DECLARE
    PRAGMA AUTONOMOUS_TRANSACTION;
    V_DEPARTMENT_ID ORG_DEPARTMENT.DEPARTMENT_ID%TYPE;
    V_PARENT_ID ORG_DEPARTMENT.PARENT_ID%TYPE;
    V_HIER_ORDER    ORG_DEPARTMENT.HIER_ORDER%TYPE;
BEGIN
  IF INSERTING OR UPDATING(''PARENT_ID'') THEN
    V_DEPARTMENT_ID := :NEW.DEPARTMENT_ID;
    V_PARENT_ID := :NEW.PARENT_ID;

    IF V_PARENT_ID IS NOT NULL THEN
      SELECT
        CASE WHEN LTRIM(SYS_CONNECT_BY_PATH(D.DEPARTMENT_ID, ''.''), ''.'') IS NULL THEN TO_CHAR(V_DEPARTMENT_ID)
        ELSE LTRIM(SYS_CONNECT_BY_PATH(D.DEPARTMENT_ID, ''.''), ''.'') || ''.'' || V_DEPARTMENT_ID END INTO V_HIER_ORDER
      FROM ORG_DEPARTMENT D
      WHERE D.DEPARTMENT_ID = V_PARENT_ID
      START WITH D.PARENT_ID IS NULL
      CONNECT BY PRIOR D.DEPARTMENT_ID = D.PARENT_ID;
      
      :NEW.HIER_ORDER := V_HIER_ORDER;
    ELSE 
      :NEW.HIER_ORDER := V_DEPARTMENT_ID;
    END IF;
    
  END IF;
END;

/
ALTER TRIGGER "DEVPROD"."TRIG_ORG_DEPARTMENT_HIER_ORDER" DISABLE;');
INSERT INTO "cloud_customer_uat"."tmp_export_table_ddl" VALUES ('TRG_ORG_DEPARTMENT_COMPANY', 'TRIGGER', '
  CREATE OR REPLACE EDITIONABLE TRIGGER "DEVPROD"."TRG_ORG_DEPARTMENT_COMPANY" 
 BEFORE INSERT OR UPDATE ON ORG_DEPARTMENT
 FOR EACH ROW
DECLARE
 V_OLD_COMPANY_DEPARTMENT_ID NUMBER(18);
 V_NEW_COMPANY_DEPARTMENT_ID NUMBER(18);
 PRAGMA AUTONOMOUS_TRANSACTION;
BEGIN
 V_OLD_COMPANY_DEPARTMENT_ID := :NEW.COMPANY_DEPARTMENT_ID;
 IF :NEW.IS_COMPANY = 1 THEN V_NEW_COMPANY_DEPARTMENT_ID := :NEW.DEPARTMENT_ID;
 ELSE
  SELECT
   MAX(DEPARTMENT_ID) INTO V_NEW_COMPANY_DEPARTMENT_ID
  FROM
   (
    SELECT
     DEPARTMENT_ID,
     RANK() OVER(ORDER BY LEVEL) AS RNK
    FROM
     ORG_DEPARTMENT
    WHERE
     IS_COMPANY = 1
     START WITH DEPARTMENT_ID = :NEW.DEPARTMENT_ID
     CONNECT BY NOCYCLE PRIOR PARENT_ID = DEPARTMENT_ID
   )
  WHERE
   RNK = 1;
 END IF;
 :NEW.COMPANY_DEPARTMENT_ID := COALESCE(V_NEW_COMPANY_DEPARTMENT_ID, V_OLD_COMPANY_DEPARTMENT_ID, :NEW.DEPARTMENT_ID);
END;
/
ALTER TRIGGER "DEVPROD"."TRG_ORG_DEPARTMENT_COMPANY" DISABLE;');
