-- View structure for vw_customer_proxy_config
-- ----------------------------
DROP VIEW IF EXISTS "cloud_customer_uat"."vw_customer_proxy_config";
CREATE VIEW "cloud_customer_uat"."vw_customer_proxy_config" AS  SELECT cpc.id,
    cpc.proxy_meta_data_id,
    cpc.action_meta_data_id
   FROM customer_proxy_config cpc
  WHERE cpc.is_default = 1::numeric
UNION ALL
 SELECT '1000000000000'::bigint::numeric + mpm.id AS id,
    mpm.src_meta_data_id AS proxy_meta_data_id,
    mpm.trg_meta_data_id AS action_meta_data_id
   FROM cloud_platform_uat.meta_proxy_map mpm
  WHERE mpm.is_default = 1::numeric AND NOT (mpm.src_meta_data_id IN ( SELECT cpc.proxy_meta_data_id
           FROM customer_proxy_config cpc
          WHERE cpc.is_default = 1::numeric));

-- ----------------------------
-- View structure for vw_user
-- ----------------------------
DROP VIEW IF EXISTS "cloud_customer_uat"."vw_user";
CREATE VIEW "cloud_customer_uat"."vw_user" AS  SELECT ur.user_id,
    ur.person_id,
    ur.employee_id,
    ur.employee_key_id,
    ur.username,
    ur.last_name,
    ur.first_name,
    ur.state_reg_number,
    ur.employee_mobile,
    ur.employee_phone,
    ur.employee_email,
    ek.position_name,
    ek.department_name,
    ek.department_id,
    ur.picture,
    ur.emp_picture,
    ur.employee_code,
    ur.password_hash,
    ur.password_expiry_date,
    ur.language_id,
    ur.mobile_password_hash,
    (substr(ur.last_name::text, 0::numeric, 1::numeric) || '.'::text) || ur.first_name::text AS employee_name
   FROM ( SELECT um.user_id,
            bp.person_id,
            emp.employee_id,
            emp.employee_code,
            emp.employee_mobile,
            nvl(emp.employee_phone, um.phone_number) AS employee_phone,
            nvl(emp.employee_email, um.email) AS employee_email,
            um.username,
            bp.last_name,
            bp.first_name,
            bp.state_reg_number,
            ( SELECT max(hrm_employee_key.employee_key_id) AS max
                   FROM hrm_employee_key
                  WHERE hrm_employee_key.employee_id = emp.employee_id AND hrm_employee_key.is_active = 1::numeric
                  GROUP BY hrm_employee_key.employee_id) AS employee_key_id,
            emp.picture,
            emp.picture AS emp_picture,
            um.password_hash,
            um.password_expiry_date,
            um.language_id,
            um.mobile_password_hash
           FROM um_system_user um
             JOIN base_person bp ON bp.person_id = um.person_id
             LEFT JOIN hrm_employee emp ON emp.person_id = um.person_id
          WHERE um.inactive = 0::numeric OR um.inactive IS NULL) ur
     LEFT JOIN ( SELECT ek_1.employee_key_id,
            pos.position_name,
            pos.position_id,
            dep.department_id,
            dep.department_name
           FROM hrm_employee_key ek_1
             LEFT JOIN hrm_position_key pk ON ek_1.position_key_id = pk.position_key_id
             LEFT JOIN hrm_position pos ON pk.position_id = pos.position_id
             LEFT JOIN org_department dep ON pk.department_id = dep.department_id) ek ON ek.employee_key_id = ur.employee_key_id;

